mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status},
where id = #{id}

mapper.select:
SELECT d.*, c.name as categoryName FROM dish d JOIN category c on d.category_id = c.id

    and d.name like CONCAT("%", #{name}, "%")
    and d.category_id = #{categoryId}
    and d.status = #{status}

select * from dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select category_id from dish

    id in

        #{id}

select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
